package com.EcoMove.Controladores;import com.EcoMove.Entidades.Estacion;import com.EcoMove.InterfaceServices.IEstacionService;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/estaciones")public class EstacionControlador {    private final IEstacionService estacionService;    public EstacionControlador(IEstacionService estacionService) {        this.estacionService = estacionService;    }    @PostMapping    public ResponseEntity<Estacion> crear(@RequestBody Estacion e) {        return ResponseEntity.ok(estacionService.crear(e));    }    @GetMapping    public ResponseEntity<List<Estacion>> listar() {        return ResponseEntity.ok(estacionService.listar());    }    @GetMapping("/{id}")    public ResponseEntity<Estacion> buscar(@PathVariable String id) {        return estacionService.buscarPorId(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}