package com.EcoMove.Controladores;import com.EcoMove.Entidades.Pago;import com.EcoMove.InterfaceServices.IPagoService;import com.EcoMove.Repositorios.PagoRepositorio;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/pagos")public class PagoController {    private final IPagoService pagoService;    public PagoController(IPagoService pagoService) {        this.pagoService = pagoService;    }    @PostMapping    public ResponseEntity<Pago> registrar(@RequestBody Pago pago) {        return ResponseEntity.ok(pagoService.registrar(pago));    }    @GetMapping    public ResponseEntity<List<Pago>> listar() {        return ResponseEntity.ok(pagoService.listar());    }    @GetMapping("/{id}")    public ResponseEntity<Pago> buscar(@PathVariable String id) {        return pagoService.buscarPorId(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}