package com.EcoMove.Controladores;import com.EcoMove.Entidades.Transporte;import com.EcoMove.InterfaceServices.ITransporteService;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/transportes")public class TransporteController {    private final ITransporteService transporteService;    public TransporteController(ITransporteService transporteService) {        this.transporteService = transporteService;    }    @PostMapping    public ResponseEntity<Transporte> crear(@RequestBody Transporte t) {        return ResponseEntity.ok(transporteService.crear(t));    }    @GetMapping    public ResponseEntity<List<Transporte>> listar() {        return ResponseEntity.ok(transporteService.listar());    }    @GetMapping("/{id}")    public ResponseEntity<Transporte> buscar(@PathVariable String id) {        return transporteService.buscarPorId(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}