package com.EcoMove.Entidades;import lombok.Data;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import java.time.LocalDateTime;@Data@Document("reembolsos")public class Reembolso {    @Id    private String id;    private String usuarioId;    private String prestamoId;    private double monto;    private LocalDateTime fecha;    private String metodo;}