package com.EcoMove.ImplementService;import com.EcoMove.Entidades.Estacion;import com.EcoMove.InterfaceServices.IEstacionService;import com.EcoMove.Repositorios.EstacionRepositorio;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class EstacionService implements IEstacionService {    private final EstacionRepositorio estacionRepositorio;    public EstacionService(EstacionRepositorio estacionRepositorio) {        this.estacionRepositorio = estacionRepositorio;    }    @Override    public Estacion crear(Estacion e) {        return estacionRepositorio.save(e);    }    @Override    public List<Estacion> listar() {        return estacionRepositorio.findAll();    }    @Override    public Optional<Estacion> buscarPorId(String id) {        return estacionRepositorio.findById(id);    }}