package com.EcoMove.ImplementService;import com.EcoMove.Entidades.Reembolso;import com.EcoMove.InterfaceServices.IReembolsoService;import com.EcoMove.Repositorios.ReembolsoRepositorio;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ReembolsoService implements IReembolsoService {    private final ReembolsoRepositorio reembolsoRepo;    public ReembolsoService(ReembolsoRepositorio reembolsoRepo) {        this.reembolsoRepo = reembolsoRepo;    }    @Override    public Reembolso registrar(Reembolso reembolso) {       return reembolsoRepo.save(reembolso);    }    @Override    public List<Reembolso> historialPorUsuario(String usuarioId) {        return reembolsoRepo.findByUsuarioId(usuarioId);    }    @Override    public List<Reembolso> historialPorPrestamo(String prestamoId) {        return reembolsoRepo.findByPrestamoId(prestamoId);    }}