package com.EcoMove.ImplementService;import com.EcoMove.Entidades.Transporte;import com.EcoMove.InterfaceServices.ITransporteService;import com.EcoMove.Repositorios.TransporteRepositorio;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class TransporteService implements ITransporteService {    private final TransporteRepositorio transporteRepositorio;    public TransporteService(TransporteRepositorio transporteRepositorio) {        this.transporteRepositorio = transporteRepositorio;    }    @Override    public Transporte crear(Transporte e) {        return transporteRepositorio.save(e);    }    @Override    public List<Transporte> listar() {        return transporteRepositorio.findAll();    }    @Override    public Optional<Transporte> buscarPorId(String id) {        return transporteRepositorio.findById(id);    }}